-module(cpn_mask_word).

-export([check/1,checkRes/1, test/0]).


check(Str) ->
    check(Str, byte_size(Str), get_mask_words()).


%% 4000ms
check(Str, _StrLen, []) -> Str;
check(Str, StrLen, [MastWord | R]) ->
    case binary:match(Str, MastWord) of
        nomatch -> check(Str, StrLen, R);
        {Index, Len} ->
            First = binary:part(Str, 0, Index),
            Last = binary:part(Str, Index + Len, StrLen - Index - Len),
            NewStr = <<First/binary, "***", Last/binary>>,
            check(NewStr, byte_size(NewStr), R)
    end.




%%[Str, Res]
%% Res::true=带敏感字，false=不带敏感字
checkRes(Str) ->
    checkRes(Str, byte_size(Str), get_mask_words(),false).

checkRes(Str, _StrLen, [], Res) -> [Str, Res];
checkRes(Str, StrLen, [MastWord | R], Res) ->
    case binary:match(Str, MastWord) of
        nomatch -> checkRes(Str, StrLen, R, Res);
        {Index, Len} ->
            First = binary:part(Str, 0, Index),
            Last = binary:part(Str, Index + Len, StrLen - Index - Len),
            NewStr = <<First/binary, "***", Last/binary>>,
            checkRes(NewStr, byte_size(NewStr), R, true)
    end.


test() ->
    T1 = erlang:monotonic_time(),
    AAA = test(1),
    T2 = erlang:monotonic_time(),
    Time = erlang:convert_time_unit(T2 - T1, native, micro_seconds),
    io:format("111:~p~n222:~p~n", [AAA, Time]).

test(1000) -> check(<<"我么那都由一个家名字叫中国，wogan，wocao"/utf8>>);
test(N) ->
    check(<<"我么那都由一个家名字叫中国，wogan，wocao"/utf8>>),
    test(N + 1).

